import  { useState,useEffect } from 'react';
import { Link } from 'react-router-dom'; 



interface Product {
    id: number;
    title: string;
    price: number;
    image: string;
    description: string;
  }

export default function Home() {
  const [currentdata, setData] = useState<Product[]>([]);
  // const [cart, setCart] = useState<Product[]>([]); 

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      });
  }, []);

  const addToCart = (product: Product) => {
 
    const storedCart = localStorage.getItem('cart');

  const currentCart = storedCart ? JSON.parse(storedCart) : [];

  const existingProductIndex = currentCart.findIndex((item: Product) => item.id === product.id);

  if (existingProductIndex !== -1) {
    currentCart[existingProductIndex].quantity += 1;
  } else {
    const cartItem = { ...product, quantity: 1 };
    currentCart.push(cartItem);
  }

  // Store the updated cart data in localStorage
  localStorage.setItem('cart', JSON.stringify(currentCart));

  alert('Product added to cart!');

  };

  // console.log(currentdata);

  return (
    <>
      <div>
        {currentdata && currentdata.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Title</th>
                <th>Image</th>
                <th>Price</th>
                <th>Description</th>
                <th>Action</th> 
              </tr>
            </thead>
            <tbody>
              {currentdata.map((data) => (
                <tr key={data.id}>
                  <td>{data.id}</td>
                  <td>{data.title}</td>
                  <td>
                    <img src={data.image} alt={data.title} style={{ width: '100px' }} />
                  </td>
                  <td>{data.price}</td>
                  <td>{data.description}</td>
                  <td>
                    <button onClick={() => addToCart(data)}>Add to Cart</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Loading data...</p>
        )}
        <Link to="/cart">Go to Cart</Link> 
      </div>
    </>
  );
}
