import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link to navigate to the cart page

// ... (previous code)



export default function Home() {
  const [currentdata, setData] = useState<Product[]>([]);
  const [cart, setCart] = useState<Product[]>([]); // Initialize an empty cart

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      });
  }, []);

  // Function to add a product to the cart
  const addToCart = (product: Product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  console.log(currentdata);

  return (
    <>
      <div>
        {currentdata && currentdata.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Title</th>
                <th>Image</th>
                <th>Price</th>
                <th>Description</th>
                <th>Action</th> {/* Add a new table header for the "Add to Cart" button */}
              </tr>
            </thead>
            <tbody>
              {currentdata.map((data) => (
                <tr key={data.id}>
                  <td>{data.id}</td>
                  <td>{data.title}</td>
                  <td>
                    <img src={data.image} alt={data.title} style={{ width: '100px' }} />
                  </td>
                  <td>{data.price}</td>
                  <td>{data.description}</td>
                  <td>
                    <button onClick={() => addToCart(data)}>Add to Cart</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Loading data...</p>
        )}
        <Link to="/cart">Go to Cart</Link> {/* Add a link to the cart page */}
      </div>
    </>
  );
}
