import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "./auth";
const SignUp = () => {
  let registeredUsers = [];
  const localStorageData = localStorage.getItem("registeredUsers");

  const { user, setUserSignUp } = useAuth();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    email: "",
    password: "",
    confirmpassword:"",
  });
  const newUser = {
    password: "",
    email: "",
    // Add other user data properties here
  };

  const handleSignUp = () => {
    const signupSuccess = true; 

    if(formData.password!=formData.confirmpassword)
    {
alert('password not match!');
    }
    if (signupSuccess) {
      newUser.email = formData.email;
      newUser.password = formData.password;
      navigate("/Login");
      registeredUsers.push(newUser);
      localStorage.setItem("registerduser", JSON.stringify(registeredUsers));
      localStorage.setItem("user", "true");

    } else {

    }
  };

  return (
    <>
    <h1>Welcome to SignUp Page</h1>
    <form>
    <div>
      <label htmlFor="email">email:</label>
      <input
        type="email"
        id="email"
        name="email"
        value={formData.email}
        onChange={(e) =>
          setFormData({ ...formData, email: e.target.value })
        }
      />
    </div>
    <br />
    <div>
      <label htmlFor="password">Password:</label>
      <input
        type="password"
        id="password"
        name="password"
        value={formData.password}
        onChange={(e) =>
          setFormData({ ...formData, password: e.target.value })
        }
      />
    </div>
    <br></br>
    <div>
      <label htmlFor="confirmpassword">Confirm Password:</label>
      <input
        type="password"
        id="confirmpassword"
        name="confirmpassword"
        value={formData.confirmpassword}
        onChange={(e) =>
          setFormData({ ...formData, confirmpassword: e.target.value })
        }
      />
    </div>
    <br />
    <button type="button" onClick={handleSignUp}>
      SignUp
    </button>
  </form>
  </>
  )

}

export default SignUp
