import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

interface Product {
  id: number;
  title: string;
  price: number;
  image: string;
  description: string;
}

export default function Home() {
  const [currentdata, setData] = useState<Product[]>([]);

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      });
  }, []);

  const addToCart = (product: Product) => {
    const existingCart = JSON.parse(localStorage.getItem('cart') || '[]');
    const productInCart = existingCart.find((item: any) => item.id === product.id);

    if (productInCart) {
      productInCart.quantity += 1;
    } else {
      existingCart.push({ ...product, quantity: 1 });
    }

    localStorage.setItem('cart', JSON.stringify(existingCart));
    alert("Product added to cart!");
  };

  return (
    <>
      <div>
        {currentdata && currentdata.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Title</th>
                <th>Image</th>
                <th>Price</th>
                <th>Description</th>
                <th>Action</th>
                <th>Quantity</th>
              </tr>
            </thead>
            <tbody>
              {currentdata.map((data) => (
                <tr key={data.id}>
                  <td>{data.id}</td>
                  <td>{data.title}</td>
                  <td>
                    <img src={data.image} alt={data.title} style={{ width: '100px' }} />
                  </td>
                  <td>{data.price}</td>
                  <td>{data.description}</td>
                  <td>
                    <button onClick={() => addToCart(data)}>Add to Cart</button>
                  </td>
                  <td>{data.quantity || 0}</td> {/* Display quantity or default to 0 */}
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Loading data...</p>
        )}
        <Link to="/cart">Go to Cart</Link>
      </div>
    </>
  );
}
