import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "./auth";

export default function Home() {
  const { user, setUser } = useAuth();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  const handleSignup = () => {
    // Perform signup logic here, e.g., send a request to your backend API
    // with the formData.
    // After successful signup, set the user state and navigate to the homepage.
    // Replace the following with your actual signup logic.
    // This is just a placeholder example.
    const signupSuccess = true; // Replace this with the actual result of signup

    if (signupSuccess) {
      setUser(true);
      navigate("/");
      localStorage.setItem("user", "true");
    } else {
      // Handle signup failure, e.g., show an error message to the user.
    }
  };

  return (
    <div>
      <h1>Signup</h1>
      <form>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={(e) =>
              setFormData({ ...formData, username: e.target.value })
            }
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={(e) =>
              setFormData({ ...formData, password: e.target.value })
            }
          />
        </div>
        <br>
        </br>
        <br></br>
        <button type="button" onClick={handleSignup}>
          Signup
        </button>
      </form>
    </div>
  );
}
